DECLARE @Ncount INT;
EXEC [ods].[ctl_check_Data_Current_Up_To_Date]  @{pipeline().parameters.configId}, @Ncount OUTPUT;
SELECT @Ncount AS Ncount;

DECLARE @startExecutionId INT;
DECLARE @endExecutionId INT;
DECLARE @odsTableName varchar(30);

EXEC [ods].[ctl_get_ExecutionLogId_Information] @{variables('configId')}, @startExecutionId OUTPUT, @endExecutionId OUTPUT, @odsTableName OUTPUT

SELECT @startExecutionId AS startExecutionId , @endExecutionId  AS endExecutionId, @odsTableName AS odsTableName

select * from @{variables('concurrentTransName')}
where ODSLastUpdateExecutionId > @{variables('startExecutionId')} 
and ODSLastUpdateExecutionId <= @{variables('endExcutionId')}
;
